void animation_phase(void) {
    if (anim_play_mode) {
        if(get_current_wpm() <= IDLE_SPEED){
            current_idle_frame = (current_idle_frame + 1) % IDLE_FRAMES;
            ssd1306_write_raw_P(idle[abs((IDLE_FRAMES-1)-current_idle_frame)]);
        }
        else if(get_current_wpm() > IDLE_SPEED && get_current_wpm() < TAP_SPEED){
            ssd1306_write_raw_P(prep[0]);
        }
        else {
            current_tap_frame = (current_tap_frame + 1) % TAP_FRAMES;
            ssd1306_write_raw_P(tap[abs((TAP_FRAMES-1)-current_tap_frame)]);
        }
    }
}

static void render_anim(void* p_event_data, uint16_t event_size)
{
    //如果wpm不为零
    if(anim_play_mode && get_current_wpm() != 000) {
	//每一帧的持续时间200ms，解析并更新下一帧
        animation_phase();
	if(!anim_play_mode) {
            nrf_delay_ms(10);
            ssd1306_sleep();
	}
    }
}

static void keyboard_anim_handler(void* p_context)
{
    UNUSED_PARAMETER(p_context);

    if (anim_play_mode)
            app_sched_event_put(NULL, 0, render_anim);

}

/**
 * @brief 初始化动画显示计时器
 *
 */
void anim_timer_init(void)
{
    ret_code_t err_code = app_timer_create(&m_keyboard_animation_timer,
        APP_TIMER_MODE_REPEATED,
        keyboard_anim_handler);
    APP_ERROR_CHECK(err_code);
}

static void anim_timer_start(void)
{
    ret_code_t err_code = app_timer_start(m_keyboard_animation_timer, ANIM_INTERVAL, NULL);
    APP_ERROR_CHECK(err_code);
}

void anim_timer_stop(void)
{
    ret_code_t err_code = app_timer_stop(m_keyboard_animation_timer);
    APP_ERROR_CHECK(err_code);
}

static void start_handler(void* p_event_data, uint16_t event_size)
{
    ssd1306_clr();
    ssd1306_show_all();
    ssd1306_write_raw_P(prep[0]);
    anim_timer_start();
}

static void stop_handler(void* p_event_data, uint16_t event_size)
{
    anim_timer_stop();
    ssd1306_clr();
    ssd1306_show_all();
    update_status_bar();
}
static void anim_play_handler(enum user_event event, void* arg)
{
    uint8_t arg2 = (uint32_t)arg;
    switch (event) {
    case USER_EVT_ANIM:
        switch (arg2) {
        case ANIM_START: // 初始化
	    anim_play_mode = true;
            app_sched_event_put(NULL, 0, start_handler);
            break;
	case ANIM_STOP: // 关闭动画定时器
	    anim_play_mode = false;
            app_sched_event_put(NULL, 0, stop_handler);
	    break;
        default:
            break;
        }
        break;
    default:
        break;
    }
}

EVENT_HANDLER(anim_play_handler);
